// Survey.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './survey.css';
const Survey = () => {
    const [questions, setQuestions] = useState([]);
    const [newQuestionText, setNewQuestionText] = useState('Ent');

    useEffect(() => {
        // Fetch questions from backend when component mounts
        fetchQuestions();
    }, []);

    const fetchQuestions = async () => {
        try {
            const response = await axios.get('/api/questions');
            setQuestions(response.data);
        } catch (error) {
            console.error('Error fetching questions:', error);
        }
    };

    const handleAddQuestion = async () => {
        try {
            const response = await axios.post('/api/questions', { text: newQuestionText });
            setQuestions([...questions, response.data]);
            setNewQuestionText('');
        } catch (error) {
            console.error('Error adding question:', error);
        }
    };

    const handleDeleteQuestion = async (id) => {
        try {
            await axios.delete(`/api/questions/${id}`);
            // Update state after successful deletion
            setQuestions(questions.filter(q => q._id !== id));
        } catch (error) {
            console.error('Error deleting question:', error);
        }
    };

    return (
        <div>
            <h1>Survey Questions</h1>
            <ul>
                {questions.map(question => (
                    <li key={question._id}>
                        {question.text}
                        <button onClick={() => handleDeleteQuestion(question._id)}>Delete</button>
                    </li>
                ))}
            </ul>
            <div>
                <input
                    type="text"
                    value={newQuestionText}
                    onChange={(e) => setNewQuestionText(e.target.value)}
                    placeholder="Enter new question"
                /><br></br>
                <button onClick={handleAddQuestion}>Add Question</button>
            </div>
        </div>
    );
};

export default Survey;
