// JobBoard.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './job.css';

const JobBoard = () => {
    const [jobs, setJobs] = useState([]);
    const [newJobTitle, setNewJobTitle] = useState('');
    const [newJobDescription, setNewJobDescription] = useState('');

    useEffect(() => {
        // Fetch job postings from backend when component mounts
        fetchJobs();
    }, []);

    const fetchJobs = async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/jobs');
            setJobs(response.data);
        } catch (error) {
            console.error('Error fetching jobs:', error);
        }
    };

    const handleAddJob = async () => {
        try {
            const newJob = { title: newJobTitle, description: newJobDescription };
            const response = await axios.post('http://localhost:5000/api/jobs', newJob);
            alert("Job added successfully");
            setJobs([...jobs, response.data]);
            setNewJobTitle('');
            setNewJobDescription('');
        } catch (error) {
            console.error('Error adding job:', error);
        }
    };

    const handleDeleteJob = async (id) => {
        try {
            await axios.delete(`http://localhost:5000/api/jobs/${id}`);
            // Update state after successful deletion
            setJobs(jobs.filter(job => job._id !== id));
        } catch (error) {
            console.error('Error deleting job:', error);
        }
    };

    return (
        <div>
            <h1>Job Board</h1>
            <div className="form">
                <input
                    type="text"
                    value={newJobTitle}
                    onChange={(e) => setNewJobTitle(e.target.value)}
                    placeholder="Enter job title"
                /><br></br>
                <textarea
                    value={newJobDescription}
                    onChange={(e) => setNewJobDescription(e.target.value)}
                    placeholder="Enter job description"
                ></textarea><br></br>
                <button onClick={handleAddJob}>Add Job</button>
            </div>
            <ul>
                {jobs.map(job => (
                    <li key={job._id}>
                        <h2>{job.title}</h2>
                        <p>{job.description}</p>
                        <button onClick={() => handleDeleteJob(job._id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default JobBoard;
